package test2;

public class boolean2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 5;
		
		boolean result = false;
		
		result = x > y;
		System.out.println("x > y = " + result);
		
		result = x < y;
		System.out.println("x < y = " + result);
		
		result = x >= y;
		System.out.println("x >= y = " + result);
		
		result = x <= y;
		System.out.println("x <= y = " + result);
		
		result = x == y;
		System.out.println("x == y = " + result);
		
		result = x != y;
		System.out.println("x != y = " + result);

	}

}
